import { mapGetters, mapActions } from 'vuex';
export default {
  name: 'AlertPage',
  computed: {
    ...mapGetters({
      status: 'alert/status',
      text: 'alert/text',
      type: 'alert/type'
    }),
    alert: {
      get() {
        return this.status;
      },
      set(value) {
        this.setAlert({
          status: value,
          text: 'test',
          type: 'error'
        });
      }
    }
  },
  methods: {
    ...mapActions({
      setAlert: 'alert/set'
    }),
    close() {
      this.setAlert({
        status: false
      });
    }
  }
};
